import React, { useState } from 'react';
import './App.css';

const API_KEY = process.env.REACT_APP_API_KEY;

function App() {
  const [address, setAddress] = useState('');
  const [sourceCode, setSourceCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [result, setResult] = useState(null);

  // ------------------ HÀM PHÂN TÍCH SOURCE CODE ------------------
  const scanSource = (code) => {
    if (!code) return;

    const checks = {
      'Mint toàn bộ cho Owner': /_mint\s*\(\s*owner\s*\(\s*\)\s*,\s*totalSupply\s*\)/i.test(code),
      'Có hàm mint tuỳ ý (nguy hiểm)': /function\s+mint\s*\(|_mint\s*\(/i.test(code) && !/_mint\s*\(\s*owner\s*\(\s*\)\s*,\s*totalSupply\s*\)/i.test(code),
      'Có MODE_TRANSFER_RESTRICTED': /MODE_TRANSFER_RESTRICTED|_mode|setMode/i.test(code),
      'Hook _beforeTokenTransfer (có thể chặn giao dịch)': /_beforeTokenTransfer\s*\(/i.test(code),
      'Blacklist hoặc kiểm soát ví': /(blacklist|isBlacklisted|blocklist)/i.test(code),
      'Phí hoặc thuế động (tax/fee)': /(setFee|_tax|takeFee|setBuyFee|setSellFee|enableTrading)/i.test(code),
      'Chỉ owner được phép (Owner control)': /(onlyOwner|renounceOwnership|transferOwnership)/i.test(code),
      'Giới hạn ví / giao dịch (MaxTx/MaxWallet)': /(maxTxAmount|maxWallet|_maxTxAmount)/i.test(code),
      'Có logic timestamp (có thể delay/lock)': /(block\.timestamp|launchTime|tradingOpen)/i.test(code),
    };

    // Phát hiện mẫu đặc biệt: dev có thể điều khiển bật/tắt trade
    const devControl =
      checks['Có MODE_TRANSFER_RESTRICTED'] && checks['Hook _beforeTokenTransfer (có thể chặn giao dịch)'];

    setResult({
      ...checks,
      'Phát hiện quyền kiểm soát bán/mở trade của dev': devControl,
    });
  };

  // ------------------ HÀM LẤY SOURCE CODE ------------------
  const checkToken = async (address) => {
    if (!address) return;
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const ethUrl = `https://api.etherscan.io/api?module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;
      const bscUrl = `https://api.bscscan.com/api?module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;

      const [ethRes, bscRes] = await Promise.allSettled([fetch(ethUrl), fetch(bscUrl)]);
      let source = '';

      if (ethRes.status === 'fulfilled') {
        const d = await ethRes.value.json();
        if (d?.status === '1' && d.result?.[0]?.SourceCode) {
          source += d.result[0].SourceCode + '\n';
        }
      }

      if (bscRes.status === 'fulfilled') {
        const d = await bscRes.value.json();
        if (d?.status === '1' && d.result?.[0]?.SourceCode) {
          source += d.result[0].SourceCode + '\n';
        }
      }

      if (!source) {
        setError('⚠️ Không lấy được mã nguồn. Hãy dán source code thủ công để kiểm tra.');
        setLoading(false);
        return;
      }

      setSourceCode(source);
      scanSource(source);
    } catch (e) {
      setError('Lỗi khi kiểm tra: ' + String(e));
    } finally {
      setLoading(false);
    }
  };

  const handlePasteCheck = () => {
    if (!sourceCode) return;
    scanSource(sourceCode);
  };

  // ------------------ GIAO DIỆN ------------------
  return (
    <div style={{ fontFamily: 'Inter, sans-serif', background: '#0f172a', minHeight: '100vh', color: '#fff', padding: 24 }}>
      <div style={{ maxWidth: 820, margin: '0 auto' }}>
        <h1 style={{ fontSize: 28, marginBottom: 8 }}>Token Scam Checker v2 🔍</h1>
        <p style={{ color: '#9ca3af' }}>
          Nhập địa chỉ token (BSC hoặc ETH) hoặc dán source code để kiểm tra các dấu hiệu rủi ro.
        </p>

        <div style={{ display: 'flex', gap: 8, marginTop: 16 }}>
          <input
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="Nhập địa chỉ token (vd: 0x...)"
            style={{
              flex: 1,
              padding: '10px 12px',
              borderRadius: 8,
              border: '1px solid #334155',
              background: '#1e293b',
              color: '#fff',
            }}
          />
          <button
            onClick={() => checkToken(address)}
            disabled={loading}
            style={{
              padding: '10px 16px',
              borderRadius: 8,
              background: '#2563eb',
              color: '#fff',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            {loading ? 'Đang kiểm tra...' : 'Kiểm tra'}
          </button>
        </div>

        {error && (
          <div
            style={{
              marginTop: 12,
              padding: 12,
              borderRadius: 8,
              background: '#7f1d1d',
              border: '1px solid #ef4444',
            }}
          >
            {error}
          </div>
        )}

        <div style={{ marginTop: 16 }}>
          <label htmlFor="pasteSource">Hoặc dán source code contract:</label>
          <textarea
            id="pasteSource"
            rows={10}
            value={sourceCode}
            onChange={(e) => setSourceCode(e.target.value)}
            style={{
              width: '100%',
              marginTop: 8,
              padding: 12,
              borderRadius: 8,
              border: '1px solid #334155',
              background: '#1e293b',
              color: '#fff',
            }}
          />
          <button
            onClick={handlePasteCheck}
            style={{
              marginTop: 8,
              padding: '10px 16px',
              borderRadius: 8,
              background: '#16a34a',
              color: '#fff',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Check source code
          </button>
        </div>

        {result && (
          <div style={{ marginTop: 18 }}>
            <h3 style={{ marginBottom: 8 }}>Kết quả phân tích</h3>
            <div style={{ display: 'grid', gap: 8 }}>
              {Object.entries(result).map(([k, v]) => (
                <div
                  key={k}
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: 12,
                    borderRadius: 8,
                    background: v ? 'rgba(239,68,68,0.15)' : 'rgba(34,197,94,0.15)',
                    border: v ? '1px solid #ef4444' : '1px solid #22c55e',
                    transition: '0.3s ease',
                  }}
                >
                  <div>{k}</div>
                  <div style={{ fontWeight: 'bold', color: v ? '#f87171' : '#4ade80' }}>
                    {v ? 'Nguy hiểm' : 'Không phát hiện'}
                  </div>
                </div>
              ))}
            </div>
            <p style={{ marginTop: 10, color: '#94a3b8' }}>
              ⚠️ Đây chỉ là kiểm tra nhanh bằng từ khóa. Để chắc chắn, nên audit thủ công hoặc bằng công cụ chuyên sâu.
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
