import React, { useState } from 'react';
import './App.css';

// API key duy nhất (đặt trong .env)
const API_KEY = process.env.REACT_APP_API_KEY;

function App() {
  const [address, setAddress] = useState('');
  const [sourceCode, setSourceCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [result, setResult] = useState(null);
  const [safeTokens, setSafeTokens] = useState([]);
  const [scanning, setScanning] = useState(false);

  // --- Hàm quét mã nguồn ---
  const scanSource = (code) => {
    if (!code) return {};
    return {
      'Mint toàn bộ cho owner': /_mint\s*\(\s*owner\s*\(\s*\)\s*,\s*totalSupply\s*\)/i.test(code),
      'Mode trap / Lock sell': /(_mode|setMode|MODE_TRANSFER|revert\()/i.test(code),
      'Blacklist control': /(blacklist|isBlacklisted)/i.test(code),
      'Tax / Fee control': /(setFee|takeFee|enableTrading|_tax)/i.test(code),
      'Owner only control': /(onlyOwner|renounceOwnership)/i.test(code),
      'Before Transfer Hook (có thể chặn bán)': /(_beforeTokenTransfer|MODE_TRANSFER_RESTRICTED)/i.test(code),
    };
  };

  // --- Hàm kiểm tra token từ API ---
  const checkToken = async (address) => {
    if (!address) return null;
    try {
      const ethUrl = `https://api.etherscan.io/api?module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;
      const bscUrl = `https://api.bscscan.com/api?module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;

      // chạy song song 2 API (ETH + BSC)
      const [ethRes, bscRes] = await Promise.allSettled([fetch(ethUrl), fetch(bscUrl)]);

      const parseResponse = async (res) => {
        if (res.status === 'fulfilled') {
          const data = await res.value.json();
          if (data?.status === '1' && Array.isArray(data.result) && data.result.length > 0) {
            const code = data.result[0].SourceCode;
            if (code && code.length > 0) return code;
          }
        }
        return '';
      };

      const ethSource = await parseResponse(ethRes);
      const bscSource = await parseResponse(bscRes);
      const source = ethSource + '\n' + bscSource;

      if (!source.trim()) return null;
      return scanSource(source);
    } catch {
      return null;
    }
  };

  // --- Lấy token mới nhất từ Dexscreener ---
  const fetchDexTokens = async () => {
    setLoading(true);
    setError(null);
    try {
      const [ethRes, bscRes] = await Promise.all([
        fetch('https://api.dexscreener.com/latest/dex/pairs/eth'),
        fetch('https://api.dexscreener.com/latest/dex/pairs/bsc'),
      ]);

      const ethData = await ethRes.json();
      const bscData = await bscRes.json();

      // Lấy token có thanh khoản thực (tránh rác)
      const allTokens = [
        ...(ethData.pairs || []),
        ...(bscData.pairs || []),
      ]
        .filter(p => p.liquidity?.usd > 1000)
        .slice(0, 20); // Giới hạn 20 token mới nhất

      return allTokens;
    } catch (e) {
      setError('Không lấy được danh sách token từ Dexscreener');
      return [];
    } finally {
      setLoading(false);
    }
  };

  // --- Quét tất cả token ---
  const scanAllTokens = async () => {
    setScanning(true);
    setSafeTokens([]);
    const tokens = await fetchDexTokens();
    const safeList = [];

    for (const token of tokens) {
      const addr = token.baseToken?.address;
      if (!addr) continue;
      const result = await checkToken(addr);

      // Nếu không có mint toàn bộ & không có lock sell => an toàn
      if (
        result &&
        !result['Mint toàn bộ cho owner'] &&
        !result['Mode trap / Lock sell']
      ) {
        safeList.push({
          name: token.baseToken.name,
          symbol: token.baseToken.symbol,
          address: addr,
          liquidity: token.liquidity?.usd?.toFixed(0),
          dex: token.dexId,
        });
      }

      // Tạm delay nhẹ để tránh spam API
      await new Promise(r => setTimeout(r, 800));
    }

    setSafeTokens(safeList);
    setScanning(false);
  };

  // --- Giao diện chính ---
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: '#0f172a', minHeight: '100vh', color: '#fff', padding: 24 }}>
      <div style={{ maxWidth: 860, margin: '0 auto' }}>
        <h1 style={{ fontSize: 28, marginBottom: 8 }}>Dexscreener Token Safety Checker 🧩</h1>
        <p style={{ color: '#9ca3af' }}>
          Quét token mới trên Dexscreener (ETH + BSC) và hiển thị những token <b>không có dấu hiệu mint toàn bộ hoặc lock sell</b>.
        </p>

        <button
          onClick={scanAllTokens}
          disabled={scanning}
          style={{
            marginTop: 16,
            padding: '10px 16px',
            borderRadius: 8,
            background: '#2563eb',
            color: '#fff',
            border: 'none',
          }}
        >
          {scanning ? 'Đang quét token...' : 'Quét toàn bộ token mới'}
        </button>

        {error && (
          <div style={{ marginTop: 12, padding: 12, background: '#7f1d1d', borderRadius: 8 }}>
            {error}
          </div>
        )}

        {safeTokens.length > 0 && (
          <div style={{ marginTop: 24 }}>
            <h3>✅ Token an toàn (không phát hiện mint toàn bộ / lock sell)</h3>
            <table style={{ width: '100%', marginTop: 8, borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ background: '#1e293b', textAlign: 'left' }}>
                  <th style={{ padding: 8 }}>Tên</th>
                  <th style={{ padding: 8 }}>Ký hiệu</th>
                  <th style={{ padding: 8 }}>Địa chỉ</th>
                  <th style={{ padding: 8 }}>Thanh khoản ($)</th>
                  <th style={{ padding: 8 }}>DEX</th>
                </tr>
              </thead>
              <tbody>
                {safeTokens.map((t, i) => (
                  <tr key={i} style={{ borderBottom: '1px solid #1e293b' }}>
                    <td style={{ padding: 8 }}>{t.name}</td>
                    <td style={{ padding: 8 }}>{t.symbol}</td>
                    <td style={{ padding: 8 }}>
                      <a
                        href={`https://dexscreener.com/${t.dex}/${t.address}`}
                        target="_blank"
                        rel="noreferrer"
                        style={{ color: '#60a5fa' }}
                      >
                        {t.address.slice(0, 10)}...
                      </a>
                    </td>
                    <td style={{ padding: 8 }}>{t.liquidity}</td>
                    <td style={{ padding: 8 }}>{t.dex}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {scanning && (
          <div style={{ marginTop: 16, color: '#a3a3a3' }}>
            ⏳ Đang quét token, vui lòng chờ (mất khoảng 20–30 giây)...
          </div>
        )}

        <div style={{ marginTop: 40, padding: 12, background: '#071327', borderRadius: 8, color: '#94a3b8' }}>
          <strong>Ghi chú:</strong>
          <ul>
            <li>Chỉ quét token có thanh khoản > 1000 USD.</li>
            <li>Dùng regex phát hiện 2 dấu hiệu chính: mint toàn bộ & lock sell.</li>
            <li>API Etherscan và Bscscan có thể bị giới hạn — chờ vài giây giữa các lượt quét.</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
