import React, { useState } from 'react';
import { Light as SyntaxHighlighter } from 'react-syntax-highlighter';
import { atomOneDark } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import './App.css';

// API key duy nh·∫•t (ƒë·∫∑t trong .env)
const API_KEY = process.env.REACT_APP_API_KEY;

function App() {
  const [address, setAddress] = useState('');
  const [sourceCode, setSourceCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [highlights, setHighlights] = useState([]);
  const [filter, setFilter] = useState('All');

  // C√°c t·ª´ kh√≥a nguy hi·ªÉm
  const KEYWORDS = {
    'Mode trap / Lock sell': /(_mode|setMode|MODE_TRANSFER|revert\()/i,
    'Blacklist control': /(blacklist|isBlacklisted)/i,
    'Tax / Fee control': /(setFee|takeFee|enableTrading|_tax)/i,
    'Owner only control': /(onlyOwner|renounceOwnership)/i,
    'Before Transfer Hook': /(_beforeTokenTransfer|_transfer\()/i,
  };

  const scanSource = (code) => {
    if (!code) return;
    const lines = code.split('\n');
    const hl = [];

    lines.forEach((line, idx) => {
      Object.entries(KEYWORDS).forEach(([key, regex]) => {
        if (regex.test(line)) {
          hl.push({ line: idx, type: key });
        }
      });
    });

    setHighlights(hl);
  };

  const checkToken = async (address) => {
    if (!address) return;
    setLoading(true);
    setError(null);
    setHighlights([]);
    setSourceCode('');

    try {
      const ethUrl = `https://api.etherscan.io/v2/api?chainid=1&module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;
      const bscUrl = `https://api.etherscan.io/v2/api?chainid=56&module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;

      const [ethRes, bscRes] = await Promise.allSettled([fetch(ethUrl), fetch(bscUrl)]);

      let source = '';

      if (ethRes.status === 'fulfilled') {
        const d = await ethRes.value.json();
        if (d?.status === '1' && d.result?.[0]?.SourceCode) {
          source += d.result[0].SourceCode + '\n';
        }
      }

      if (bscRes.status === 'fulfilled') {
        const d = await bscRes.value.json();
        if (d?.status === '1' && d.result?.[0]?.SourceCode) {
          source += d.result[0].SourceCode + '\n';
        }
      }

      if (!source) {
        setError('Kh√¥ng l·∫•y ƒë∆∞·ª£c m√£ ngu·ªìn t·ª´ Etherscan V2. H√£y d√°n source code b√™n d∆∞·ªõi ƒë·ªÉ check.');
        setLoading(false);
        return;
      }

      setSourceCode(source);
      scanSource(source);
    } catch (e) {
      setError('L·ªói khi ki·ªÉm tra: ' + String(e));
    } finally {
      setLoading(false);
    }
  };

  const handlePasteCheck = () => {
    scanSource(sourceCode);
  };

  // X√°c ƒë·ªãnh highlight t·ª´ng d√≤ng theo filter
  const lineProps = (lineNumber) => {
    const hlFiltered =
      filter === 'All'
        ? highlights
        : highlights.filter((h) => h.type === filter);

    const isHighlighted = hlFiltered.some((h) => h.line === lineNumber - 1);

    return {
      style: isHighlighted
        ? { display: 'block', backgroundColor: '#3f1d1d' }
        : { display: 'block' },
    };
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: '#0f172a', minHeight: '100vh', color: '#fff', padding: 24 }}>
      <div style={{ maxWidth: 900, margin: '0 auto' }}>
        <h1 style={{ fontSize: 28, marginBottom: 8 }}>Token Scam Checker IDE üîç</h1>
        <p style={{ color: '#9ca3af' }}>
          Nh·∫≠p ƒë·ªãa ch·ªâ token (BSC ho·∫∑c ETH) ho·∫∑c d√°n source code ƒë·ªÉ check v√† highlight c√°c d√≤ng nguy hi·ªÉm.
        </p>

        <div style={{ display: 'flex', gap: 8, marginTop: 16 }}>
          <input
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="Nh·∫≠p ƒë·ªãa ch·ªâ token (vd: 0x...)"
            style={{ flex: 1, padding: '10px 12px', borderRadius: 8, border: '1px solid #334155' }}
          />
          <button
            onClick={() => checkToken(address)}
            disabled={loading}
            style={{ padding: '10px 16px', borderRadius: 8, background: '#2563eb', color: '#fff', border: 'none' }}
          >
            {loading ? 'ƒêang ki·ªÉm tra...' : 'Ki·ªÉm tra'}
          </button>
        </div>

        {error && (
          <div style={{ marginTop: 12, padding: 12, background: '#7f1d1d', borderRadius: 8 }}>
            {error}
          </div>
        )}

        <div style={{ marginTop: 16 }}>
          <label htmlFor="pasteSource">D√°n source code contract:</label>
          <textarea
            id="pasteSource"
            rows={10}
            value={sourceCode}
            onChange={(e) => setSourceCode(e.target.value)}
            style={{
              width: '100%',
              marginTop: 8,
              padding: 12,
              borderRadius: 8,
              border: '1px solid #334155',
              background: '#1e293b',
              color: '#fff',
              fontFamily: 'monospace',
            }}
          />
          <button
            onClick={handlePasteCheck}
            style={{ marginTop: 8, padding: '10px 16px', borderRadius: 8, background: '#16a34a', color: '#fff', border: 'none' }}
          >
            Scan & Highlight
          </button>
        </div>

        {highlights.length > 0 && (
          <div style={{ marginTop: 16 }}>
            <label htmlFor="filter">L·ªçc lo·∫°i r·ªßi ro:</label>
            <select
              id="filter"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              style={{ marginLeft: 8, padding: '6px 12px', borderRadius: 6, background: '#1e293b', color: '#fff', border: '1px solid #334155' }}
            >
              <option>All</option>
              {Object.keys(KEYWORDS).map((k) => (
                <option key={k}>{k}</option>
              ))}
            </select>
          </div>
        )}

        {sourceCode && (
          <div style={{ marginTop: 18 }}>
            <h3 style={{ marginBottom: 8 }}>Source code highlight</h3>
            <div style={{ maxHeight: 500, overflowY: 'auto', borderRadius: 8, border: '1px solid #334155' }}>
              <SyntaxHighlighter
                language="solidity"
                style={atomOneDark}
                showLineNumbers
                wrapLines
                lineProps={lineProps}
              >
                {sourceCode}
              </SyntaxHighlighter>
            </div>
          </div>
        )}

        <div style={{ marginTop: 24, padding: 12, background: '#071327', borderRadius: 8, color: '#94a3b8' }}>
          <strong>H∆∞·ªõng d·∫´n nhanh:</strong>
          <ol>
            <li>ƒê·∫∑t API key duy nh·∫•t v√†o <code>.env</code> v·ªõi t√™n <code>REACT_APP_API_KEY</code>.</li>
            <li>D√°n source code n·∫øu token ch∆∞a verified.</li>
            <li>Ch·ªçn filter lo·∫°i r·ªßi ro ƒë·ªÉ highlight c√°c d√≤ng t∆∞∆°ng ·ª©ng.</li>
            <li>Nh·ªØng d√≤ng highlight l√† c√°c v·ªã tr√≠ nguy hi·ªÉm. Audit k·ªπ t·ª´ng d√≤ng ƒë·ªÉ ƒë√°nh gi√° r·ªßi ro.</li>
          </ol>
        </div>
      </div>
    </div>
  );
}

export default App;
