import React, { useState } from 'react';
import './App.css';

// API key duy nh·∫•t (ƒë·∫∑t trong .env)
const API_KEY = process.env.REACT_APP_API_KEY;

function App() {
  const [address, setAddress] = useState('');
  const [sourceCode, setSourceCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [result, setResult] = useState(null);

  const scanSource = (code) => {
    if (!code) return;
    const checks = {
      'Mode trap / Lock sell': /(_mode|setMode|MODE_TRANSFER|revert\()/i.test(code),
      'Blacklist control': /(blacklist|isBlacklisted)/i.test(code),
      'Tax / Fee control': /(setFee|takeFee|enableTrading|_tax)/i.test(code),
      'Owner only control': /(onlyOwner|renounceOwnership)/i.test(code),
      'Before Transfer Hook': /(_beforeTokenTransfer|_transfer\()/i.test(code),
    };
    setResult(checks);
  };

  const checkToken = async (address) => {
    if (!address) return;
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      // Endpoint Etherscan V2 cho ETH v√† BSC
      const ethUrl = `https://api.etherscan.io/v2/api?chainid=1&module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;
      const bscUrl = `https://api.etherscan.io/v2/api?chainid=56&module=contract&action=getsourcecode&address=${address}&apikey=${API_KEY}`;

      const [ethRes, bscRes] = await Promise.allSettled([fetch(ethUrl), fetch(bscUrl)]);

      let source = '';

      if (ethRes.status === 'fulfilled') {
        const d = await ethRes.value.json();
        if (d?.status === '1' && d.result?.[0]?.SourceCode) {
          source += d.result[0].SourceCode + '\n';
        }
      }

      if (bscRes.status === 'fulfilled') {
        const d = await bscRes.value.json();
        if (d?.status === '1' && d.result?.[0]?.SourceCode) {
          source += d.result[0].SourceCode + '\n';
        }
      }

      if (!source) {
        setError('Kh√¥ng l·∫•y ƒë∆∞·ª£c m√£ ngu·ªìn t·ª´ Etherscan V2. H√£y d√°n source code b√™n d∆∞·ªõi ƒë·ªÉ check.');
        setLoading(false);
        return;
      }

      setSourceCode(source);
      scanSource(source);
    } catch (e) {
      setError('L·ªói khi ki·ªÉm tra: ' + String(e));
    } finally {
      setLoading(false);
    }
  };

  const handlePasteCheck = () => {
    if (!sourceCode) return;
    scanSource(sourceCode);
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: '#0f172a', minHeight: '100vh', color: '#fff', padding: 24 }}>
      <div style={{ maxWidth: 820, margin: '0 auto' }}>
        <h1 style={{ fontSize: 28, marginBottom: 8 }}>Token Scam Checker Mini üîç</h1>
        <p style={{ color: '#9ca3af' }}>
          Nh·∫≠p ƒë·ªãa ch·ªâ token (BSC ho·∫∑c ETH) ho·∫∑c d√°n source code ƒë·ªÉ ki·ªÉm tra d·∫•u hi·ªáu r·ªßi ro.
        </p>

        <div style={{ display: 'flex', gap: 8, marginTop: 16 }}>
          <input
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="Nh·∫≠p ƒë·ªãa ch·ªâ token (vd: 0x...)"
            style={{ flex: 1, padding: '10px 12px', borderRadius: 8, border: '1px solid #334155' }}
          />
          <button
            onClick={() => checkToken(address)}
            disabled={loading}
            style={{ padding: '10px 16px', borderRadius: 8, background: '#2563eb', color: '#fff', border: 'none' }}
          >
            {loading ? 'ƒêang ki·ªÉm tra...' : 'Ki·ªÉm tra'}
          </button>
        </div>

        {error && (
          <div style={{ marginTop: 12, padding: 12, background: '#7f1d1d', borderRadius: 8 }}>
            {error}
          </div>
        )}

        <div style={{ marginTop: 16 }}>
          <label htmlFor="pasteSource">Ho·∫∑c d√°n source code contract:</label>
          <textarea
            id="pasteSource"
            rows={10}
            value={sourceCode}
            onChange={(e) => setSourceCode(e.target.value)}
            style={{ width: '100%', marginTop: 8, padding: 12, borderRadius: 8, border: '1px solid #334155', background: '#1e293b', color: '#fff' }}
          />
          <button
            onClick={handlePasteCheck}
            style={{ marginTop: 8, padding: '10px 16px', borderRadius: 8, background: '#16a34a', color: '#fff', border: 'none' }}
          >
            Check source code
          </button>
        </div>

        {result && (
          <div style={{ marginTop: 18 }}>
            <h3 style={{ marginBottom: 8 }}>K·∫øt qu·∫£ ph√¢n t√≠ch</h3>
            <div style={{ display: 'grid', gap: 8 }}>
              {Object.entries(result).map(([k, v]) => (
                <div
                  key={k}
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: 12,
                    borderRadius: 8,
                    background: v ? '#3f1d1d' : '#073b1a',
                  }}
                >
                  <div>{k}</div>
                  <div style={{ fontWeight: 'bold', color: v ? '#fca5a5' : '#86efac' }}>
                    {v ? 'Nguy hi·ªÉm' : 'Kh√¥ng ph√°t hi·ªán'}
                  </div>
                </div>
              ))}
            </div>
            <p style={{ marginTop: 10, color: '#94a3b8' }}>
              L∆∞u √Ω: ƒê√¢y l√† ki·ªÉm tra nhanh b·∫±ng t·ª´ kh√≥a. Kh√¥ng ph·∫£i audit ƒë·∫ßy ƒë·ªß ‚Äî s·ª≠ d·ª•ng l√†m c·∫£nh b√°o ban ƒë·∫ßu.
            </p>
          </div>
        )}

        <div style={{ marginTop: 24, padding: 12, background: '#071327', borderRadius: 8, color: '#94a3b8' }}>
          <strong>H∆∞·ªõng d·∫´n nhanh:</strong>
          <ol>
            <li>ƒê·∫∑t API key duy nh·∫•t v√†o <code>.env</code> v·ªõi t√™n <code>REACT_APP_API_KEY</code>.</li>
            <li>N·∫øu token ch∆∞a verified, d√°n source code v√†o √¥ b√™n tr√™n ƒë·ªÉ check.</li>
            <li>Ki·ªÉm tra b·∫±ng t·ª´ kh√≥a ch·ªâ l√† b∆∞·ªõc ƒë·∫ßu ‚Äî audit chuy√™n s√¢u v·∫´n c·∫ßn review t·ª´ng d√≤ng code.</li>
          </ol>
        </div>
      </div>
    </div>
  );
}

export default App;
